mapper.insert:
insert into goods (GOODS_NUM,GOODS_NAME,GOODS_PRICE,GOODS_CONTENTS,VISIT_COUNT,GOODS_MAIN_IMAGE,GOODS_MAIN_STORE_IMAGE
,GOODS_DETAIL_IMAGE,GOODS_DETAIL_STORE_IMAGE,EMP_NUM,GOODS_REGIST,UPDATE_EMP_NUM,GOODS_UPDATE_DATE)
values (#{goodsNum}, #{goodsName}, #{goodsPrice}, #{goodsContents},  0 
, #{goodsMainImage}, #{goodsMainStoreImage}

, #{goodsDetailImage}, #{goodsDetailStoreImage}


, null, null

, #{empNum}
, sysdate, null, null )

mapper.select:
	select * from (select row_number() over(order by goods_num)rn,  from goods


goods_num like '%'||#{searchWord}||'%' or
goods_name like '%'||#{searchWord}||'%' or
goods_price like '%'||#{searchWord}||'%'

)
where rn between #{startRow} and #{endRow}

mapper.update:
update goods set
GOODS_NAME = #{goodsName}
,GOODS_PRICE = #{goodsPrice}
,GOODS_CONTENTS = #{goodsContents}
,UPDATE_EMP_NUM = #{updateEmpNum}
,GOODS_UPDATE_DATE = sysdate

,GOODS_MAIN_IMAGE = #{goodsMainImage}
,GOODS_MAIN_STORE_IMAGE = #{goodsMainStoreImage}


,GOODS_DETAIL_IMAGE = #{goodsDetailImage}
,GOODS_DETAIL_STORE_IMAGE = #{goodsDetailStoreImage}

where goods_num = #{goodsNum}

mapper.select:
select count(*) from (select from goods
goods_num like '%'||#{searchWord}||'%' or goods_name like '%'||#{searchWord}||'%' or goods_price like '%'||#{searchWord}||'%'
)

   select * from(
   select rownum rn, GOODS_NUM,GOODS_NAME,GOODS_PRICE,GOODS_CONTENTS,VISIT_COUNT,GOODS_MAIN_IMAGE,GOODS_MAIN_STORE_IMAGE
,GOODS_DETAIL_IMAGE,GOODS_DETAIL_STORE_IMAGE,EMP_NUM,GOODS_REGIST,UPDATE_EMP_NUM,GOODS_UPDATE_DATE
   from goods)
   where rn between #{startRow} and #{endRow}
